import React from 'react';
import FormControl from "react-bootstrap/FormControl";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import InputGroup from 'react-bootstrap/InputGroup';
import Image from "react-bootstrap/Image";

const MyInput = ({placeholder, overlayTrigger = true}) => (
    <Row>
        <Col>
            <InputGroup className="mb-1">
                    <FormControl style={{height: '50px'}} type="text" placeholder={placeholder}/>
                    {overlayTrigger ? <div className='box '>
                        <i className="arr-up"/>
                    </div>:''}
                {overlayTrigger ? <OverlayTrigger placement="right"
                                                  overlay={<Tooltip id="tooltip-disabled">Please provide a street
                                                      address, airport name or hotel name.</Tooltip>}>

                                        <span className="d-inline-block">

                                             <InputGroup.Append>

                                                 <Image alt='  ' src='https://static.talixo.de/images/icon_help.png'
                                                        style={{
                                                            pointerEvents: 'none',
                                                            margin: '5px',
                                                            background: 'transparent'
                                                        }}/>
                                             </InputGroup.Append>
                                          </span>
                </OverlayTrigger> : ''}

            </InputGroup>
        </Col>
    </Row>
);
export default MyInput;
