import React from 'react';
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import InputGroup from 'react-bootstrap/InputGroup';
import Image from "react-bootstrap/Image";

const MyInput = ({controlId, placeholder,overlayTrigger = true}) => (
    <Row>
    <InputGroup controlId={controlId} className="mb-3">


                                        <Form.Control  type="text" placeholder={placeholder}/>

                                        {overlayTrigger?<OverlayTrigger placement="right"
                                                        overlay={<Tooltip id="tooltip-disabled">Please provide a street
                                                            address, airport name or hotel name.</Tooltip>}>
                                        <span className="d-inline-block">
                                             <InputGroup.Append>
                                        `  `<Image alt='  ' src='https://static.talixo.de/images/icon_help.png'
                                               style={{pointerEvents: 'none'}}/>
                                             </InputGroup.Append>
                                          </span>
                                        </OverlayTrigger>:''}

                            </InputGroup>
    </Row>
);
export default MyInput;
