import React, {Component} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Collapse from 'react-bootstrap/Collapse';
import InputGroup from 'react-bootstrap/InputGroup';
import DatePicker from 'react-datepicker';
import './styles/App.css';
import MyInput from "./MyInput";
import MySelector from "./MySelector";
import MoreHideOptions from "./MoreHideOptions";

class App extends Component {
    state = {
        isShowMore: false,
        startDate: new Date(),
        isCheckClick: false,
        mySelectorOptions: [
            {
                img: 'https://img.icons8.com/ultraviolet/40/000000/passenger.png',
                item: [1,2,3,4,5,6,7,8]
            },
             {
                img: 'https://img.icons8.com/ultraviolet/40/000000/briefcase.png',
                item: [0,1,2,3,4,5,6,7,8]
            },
             {
                img: 'https://img.icons8.com/ultraviolet/40/000000/golf-bag.png',
                item: [0,1,2,3]
            },
             {
                img: 'https://img.icons8.com/ultraviolet/40/000000/corgi.png',
                item: [0,1,2,3,4]
            },
             {
                img: 'https://img.icons8.com/ultraviolet/40/000000/children.png',
                item: [0,1,2]
            },

            ]
    };

    handleChange(date) {
        this.setState({
            startDate: date
        });
    }
    onClickShowMore(){
         this.setState({isShowMore: !this.state.isShowMore});
    }

    render() {
        const {open} = this.state;
        return (
            <div className='App'>
            <Container style={{background: '#3f454d', height: '100vh'}}>
                <Row className='justify-content-md-center'>
                    <Col sm={6}>
                    <Form style={{maxWidth: '500px', marginTop: '50px'}}>
                        <MyInput placeholder="Pick up:"/>
                        <MyInput placeholder="Destination:"/>
                        <MyInput
                            placeholder="Voucher code (optional):"
                            overlayTrigger={false}
                        />
                        <Row>
                            <Col sm={8}>
                                <label>
                                    <DatePicker

                                                className="form-control"
                                                onChange={this.handleChange.bind(this)}
                                                minDate={new Date()}
                                                placeholderText={this.state.startDate.getDate()}
                                    />
                                    <Image alt='' src="https://img.icons8.com/ultraviolet/40/000000/today.png"
                                           style={{pointerEvents: 'none', marginBottom: '5px'}}
                                    />
                                </label>
                            </Col>
                        </Row>
                        <Row>
                            <Col md={5}>
                                <small style={{color: 'white'}}>For 1-2 passengers with 1-2 bags</small>
                            </Col>
                            <MoreHideOptions  isShowMore={this.state.isShowMore}/>

                        </Row>
                        <br/>
                        <Collapse in={this.state.isShowMore}>
                            <div id="example-collapse">
                                <Row>
                                    {this.state.mySelectorOptions.map((val, key) =>{
                                       return <MySelector key={key} img={val.img} item={val.item}/>
                                    })}
                                </Row>
                                   <Row>
                                     <Col xs={4} >
                                        <Form.Group controlId="formBasicChecbox">
                                            <Form.Check
                                                        style={{color: 'white'}}
                                                        type="checkbox"
                                                        label={this.state.isCheckClick ? "Per-hour booking for" : "Per-hour booking"}
                                                        onChange={() => this.setState({isCheckClick: !this.state.isCheckClick})}/>
                                        </Form.Group>
                                    </Col>
                                    {this.state.isCheckClick ?<MySelector
                                        img={'https://img.icons8.com/ultraviolet/40/000000/time.png'}
                                        item={[0,1,2]}
                                                                          /> :''}



                                </Row>
                            </div>
                        </Collapse>

                        <Row>
                            <Col sm={8}>
                                <Button variant="danger" size="lg" block>
                                    Start booking
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                    </Col>
                </Row>
            </Container>
            </div>
        );
    }
}

export default App;
